import React from 'react';

const ModalCamera = ({ children, onClose }) => {
    return (
        <div>
            {/* Darkening overlay covering the whole page*/}
            <div
                style={{
                    position: 'fixed',
                    top: '-50%',
                    left: '-20%',
                    width: '135%',
                    height: '100%',
                    backgroundColor: 'rgba(0, 0, 0, 0.2)',
                    zIndex: 1000,
                }}
                onClick={onClose}
            ></div>

            {/* Modal Content */}
            <div
                style={{
                    position: 'fixed',
                    top: '50%',
                    left: '50%',
                    transform: 'translate(-50%, -50%)',
                    backgroundColor: 'white',
                    padding: '20px',
                    borderRadius: '8px',
                    zIndex: 1001,
                    boxShadow: '0px 0px 30px 10px rgba(0, 0, 0, 0.3)',
                }}
            >
                {children} {/* div content */}
            </div>
        </div>
    );
};

export default ModalCamera;
