import React, { useEffect, useRef, useState } from 'react';

const CameraComponent = ({onPhotoCapture}) => {
    const videoRef = useRef(null);
    const canvasRef = useRef(null);
    const [image, setImage] = useState(null);

    useEffect(() => {
        const startCamera = async () => {
            try {
                const stream = await navigator.mediaDevices.getUserMedia({ video: true });
                if (videoRef.current) {
                    videoRef.current.srcObject = stream;
                }
            } catch (error) {
                console.error("Error accessing the camera", error);
            }
        };

        startCamera();

        // Copiez videoRef.current dans une variable locale
        const currentVideoRef = videoRef.current;

        return () => {
            // Utilisez currentVideoRef au lieu de videoRef.current
            if (currentVideoRef && currentVideoRef.srcObject) {
                const tracks = currentVideoRef.srcObject.getTracks();
                tracks.forEach(track => track.stop());
            }
        };
    }, []);

    const capturePhoto = () => {
        const canvas = canvasRef.current;
        const video = videoRef.current;

        if (canvas && video) {
            canvas.width = video.videoWidth;
            canvas.height = video.videoHeight;

            const ctx = canvas.getContext('2d');
            ctx.drawImage(video, 0, 0, canvas.width, canvas.height);

            const imageSrc = canvas.toDataURL('image/png');
            setImage(imageSrc);

            // Appeler la fonction pour passer l'image capturée
            onPhotoCapture(imageSrc);
        }
    };

    return (
        <div>
            <video ref={videoRef} autoPlay style={{ transform: 'scaleX(-1)', width: '100%', height: 'auto' }} />
            <button onClick={capturePhoto}>Prendre une photo</button>
            <canvas ref={canvasRef} style={{ display: 'none' }} />
            {image && (
                <div>
                    <h2>Photo Capturée</h2>
                    <img src={image} alt="Captured" style={{ width: '100%', height: 'auto' }} />
                </div>
            )}
        </div>
    );
};

export default CameraComponent;
