import React from 'react';
import { Link } from 'react-router-dom';
import './css/non_prescription_drugs.css'

const drugs_items = [
    { id: 1, label: 'Ordonnance' },
    { id: 2, label: 'Carte Vitale' },
    { id: 3, label: 'Carte d\'Identité' },
    { id: 4, label: 'Prescriptions' },
    { id: 5, label: 'Autre Document' }
];

function NonPrescriptionDrugs() {
  return (
    <div className="npd_App">

        {/* header container */}
        <div className="npd_header_container">

            {/* Rectangle 'go back rectangle' */}
            <Link to="/" style={{ textDecoration: 'none' }}>
                <div className="rectangle npd_back_button" style={{cursor: 'pointer'}}>
                    <p style={{ fontSize: '2.5em' }}>Retour</p>
                </div>
            </Link>

            {/* logo container */}
            <div className="npd_logo_container">
                {/* logo PharmaXcess */}
                <img
                    src={require('./../../assets/logo.png')}
                    alt="Logo PharmaXcess"
                    className="logo"
                />
            </div>

        </div>

        <div className='npd_body_page'>

            {/* Rectangle 'main rectangle' */}
            <div className="rectangle" style={{top: '5%', left: '50%', width: '60%'
                , display: 'flex', flexDirection: 'column', overflowY: 'auto'
            }}>
                <p style={{ fontSize: '3em', textAlign: 'center' }}>
                Voici la liste des médicaments <br/>
                diponibles à la vente :
                </p>
            </div>

            {/* drugs list */}
            <div
                style={{
                    position: 'relative',
                    top: '-5%',
                    width: '100%',
                    height: '60%',
                    display: 'flex',
                    flexWrap: 'wrap', // Permet aux éléments de passer à la ligne
                    justifyContent: 'center', // Centre les éléments horizontalement
                    alignContent: 'center', // Centre les éléments verticalement
                    border: 'solid black 10px',
                    overflowY: 'auto', // Rend la div scrollable verticalement si nécessaire
                    padding: '10px',
                    backgroundColor: '#f3d1d1' // Couleur de fond similaire à l'image
                }}
            >
                {drugs_items.map((item) => (
                    <div
                        key={item.id}
                        className="rectangle"
                        style={{
                            height: '150px', // Hauteur des rectangles
                            width: '200px', // Largeur des rectangles
                            margin: '10px', // Espacement entre les rectangles
                            borderRadius: '15px', // Coins arrondis comme dans l'image
                            background: 'linear-gradient(135deg, #e66465, #f24f74)', // Dégradé similaire à l'image
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                            color: 'black',
                            fontWeight: 'bold',
                            textAlign: 'center'
                        }}
                    >
                        <p style={{ fontSize: '3em', textAlign: 'center' }}>
                            {item.label}</p>
                    </div>
                ))}
            </div>

        </div>

    </div>
  );
}

export default NonPrescriptionDrugs;
