import React from 'react';
import { Link } from 'react-router-dom';
import './css/non_prescription_drugs.css'

const drugs_items = [
    { id: 1, label: 'Ordonnance' },
    { id: 2, label: 'Carte Vitale' },
    { id: 3, label: 'Carte d\'Identité' },
    { id: 4, label: 'Prescriptions' },
    { id: 5, label: 'Autre Document' }
];

function NonPrescriptionDrugs() {
  return (
    <div className="npd_App">

        {/* header container */}
        <div className="npd_header_container">

            {/* Rectangle 'go back rectangle' */}
            <Link to="/" style={{ textDecoration: 'none' }}>
                <div className="rectangle npd_back_button" style={{cursor: 'pointer'}}>
                    <p style={{ fontSize: '2.5em' }}>Retour</p>
                </div>
            </Link>

            {/* logo container */}
            <div className="npd_logo_container">
                {/* logo PharmaXcess */}
                <img
                    src={require('./../../assets/logo.png')}
                    alt="Logo PharmaXcess"
                    className="logo"
                />
            </div>

        </div>

        <div className='npd_body_page'>

            {/* Rectangle 'main rectangle' */}
            <div className="rectangle" style={{top: '5%', left: '50%', width: '60%'
                , display: 'flex', flexDirection: 'column', overflowY: 'auto'
            }}>
                <p style={{ fontSize: '3em', textAlign: 'center' }}>
                Voici la liste des médicaments <br/>
                diponibles à la vente :
                </p>
            </div>

            {/* drugs list */}
            <div style={{position: 'relative', top: '-5%'
                , width: '100%', height: '60%', display: 'flex'
                , border: 'solid black 10px'}}>

                {drugs_items.map((item) => (
                    <div
                        key={item.id} // clé unique pour chaque élément
                        className="rectangle"
                        style={{
                            position: 'relative',
                            height: '50%x', // taille fixe pour éviter les débordements
                            width: '20%',
                            top: '50%',
                            left: '10%',
                            margin: '10px auto', // centrage horizontal
                            border: 'solid 2px black',
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                        }}
                    >
                        <p style={{ fontSize: '2em' }}>{item.label}</p>
                    </div>
                ))}

                {/* Rectangle 'Carte d'Identité' */}
                <div className="rectangle" style={{position: 'relative', height: '50%'
                    , width: '20%', top: '50%', left: '35%'}}>
                    <p style={{ fontSize: '3em', textAlign: 'center'}}>
                        Carte <br/>
                        d'Identité
                    </p>
                </div>

            </div>

        </div>

    </div>
  );
}

export default NonPrescriptionDrugs;
